template.value:
import express from 'express'

const app = express()

app.use(express.json())
app.use(express.urlencoded({extended: true}))

app.get('/', (req, res) => {
    console.log('TEST')
    res.send('TEST')
})

const PORT = 3001

app.listen(PORT, () => {
    console.log('SERVER START ON PORT', PORT)
})
